    profileph() {
      if (document.getElementById("pp")) {
        document.getElementById("pp").remove;
      }

      let input = document.createElement('input');
      input.type = 'file';

      input.onchange = _ => {
        let files = Array.from(input.files);

        var loadFile = function (_event) {
          var reader = new FileReader();

          reader.onload = function () {
            let x = document.createElement('img');
            x.id = "pp";
            x.style.width = "100px";
            x.style.height = "100px";
            x.style.borderRadius = "50%";
            document.getElementById('photoprofile').appendChild(x);
            var output = document.getElementById('pp');
            output['src'] = reader.result; // @ts-ignore

            localStorage.setItem("profile-photoo", reader.result);
          };

          reader.readAsDataURL(_event.target.files[0]);
        };

        loadFile(event);
      };

      input.click();
    }

